import osfrom PIL import Image# Đường dẫn tới thư mục chứa ảnhfolder_path = './images/'# Lấy tất cả các file trong thư mục với các định dạng ảnh phổ biếnimage_files = [f for f in os.listdir(folder_path) if f.endswith(('.png', '.jpg', '.jpeg', '.gif', '.bmp'))]# Duyệt qua từng file và in tên file rafor image_file in image_files: img_path = os.path.join(folder_path, image_file) # Hiển thị tên file bao gồm cả phần mở rộng print(f'<div class="image"><img src="./images/{image_file}" alt="{image_file}"/></div>') # Mở ảnh (nếu cần xử lý hoặc kiểm tra) img = Image.open(img_path) # Bạn có thể hiển thị ảnh nếu cần (tùy chọn) # img.show()  # Dùng phương thức này để mở ảnh với trình xem ảnh mặc định trên hệ thống