import osfrom PIL import Imageimport jsonimport sysimport iosys.stdout = io.TextIOWrapper(sys.stdout.buffer, encoding='utf-8')# Đường dẫn tới thư mục chứa ảnhfolder_path = './images/'# Lấy tất cả các file trong thư mục với các định dạng ảnh phổ biếnimage_files = sorted(    [f for f in os.listdir(folder_path) if f.lower().endswith(('.png', '.jpg', '.jpeg', '.gif', '.bmp')) and os.path.isfile(os.path.join(folder_path, f))],    key=lambda x: os.path.getmtime(os.path.join(folder_path, x)),    reverse=True)with open("images.txt", "w", encoding="utf-8") as file:    file.write('')# Duyệt qua từng file và in tên file rafor image_file in image_files:    img_path = os.path.join(folder_path, image_file)    # Hiển thị tên file bao gồm cả phần mở rộng    # print(f'<div class="image"><img src="./images/{image_file}" alt="{image_file}"/></div>')    # print(image_file)    # Mở ảnh (nếu cần xử lý hoặc kiểm tra)    # img = Image.open(img_path)    name = f'{image_file}\n'    with open("images.txt", "a", encoding="utf-8") as file:        file.write(name)    # Bạn có thể hiển thị ảnh nếu cần (tùy chọn)    # img.show()  # Dùng phương thức này để mở ảnh với trình xem ảnh mặc định trên hệ thốngsys.stdout.flush()